import React, { useState, useEffect, useRef } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
} from "react-router-dom";
import "./App.css";
import Calendar from "react-calendar";
import "icheck-material/icheck-material.min.css";
import moment from "moment";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const savedLoginState = localStorage.getItem("isLoggedIn");
    setIsLoggedIn(savedLoginState === "true");
  }, []);

  const handleLoginSuccess = () => {
    setIsLoggedIn(true);
    localStorage.setItem("isLoggedIn", "true");
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem("isLoggedIn");
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<Home isLoggedIn={isLoggedIn} onLogout={handleLogout} />}
        />
        <Route
          path="/login"
          element={<Login onLoginSuccess={handleLoginSuccess} />}
        />
        <Route path="/signup" element={<SignUp />} />
      </Routes>
    </Router>
  );
}

function Home({ isLoggedIn, onLogout }) {
  
  return (
    <div className="main">


      <div id="calendar" ref={calendarRef}>
        <Calendar
          onChange={setDate}
          value={date}
          formatDay={(locale, date) => date.getDate()}
        />
      </div>
    </div>
  );
}

function Sidebar({ isLoggedIn, onLogout, togglePopup }) {
  return (
    <div id="sidebar">
      <div className="login-print">
        {isLoggedIn ? (
          <button onClick={onLogout}>로그아웃</button>
        ) : (
          <a href="/login">로그인 후 이용하세요</a>
        )}
      </div>
      <div className="profile">
        <img
          src="https://avatars.githubusercontent.com/u/144537092?v=4&size=64"
          alt="Profile"
          className="profile-image"
        />
        <div className="profile-info">
          <span className="name">김영혜</span>
          <span className="state">집에가고싶다</span>
        </div>
      </div>
      <hr />
      <div className="todolist">
        <h3>
          오늘할일 <button onClick={togglePopup}>+</button>
        </h3>
      </div>
    </div>
  );
}

function Popup({
  todoTitle,
  todoDate,
  setTodoTitle,
  setTodoDate,
  saveTodo,
  togglePopup,
}) {
  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <h4>To-do</h4>
        <label htmlFor="todo-title">제목 </label>
        <input
          type="text"
          id="todo-title"
          value={todoTitle}
          onChange={(e) => setTodoTitle(e.target.value)}
          placeholder="할 일을 입력하세요"
        />
        <br />
        <label htmlFor="todo-date">날짜 </label>
        <input
          type="date"
          id="todo-date"
          value={todoDate}
          onChange={(e) => setTodoDate(e.target.value)}
        />
        <br />
        <button onClick={saveTodo}>저장</button>
        <button onClick={togglePopup}>닫기</button>
      </div>
    </div>
  );
}

function Login({ onLoginSuccess }) {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!id || !password) return alert("아이디와 비밀번호를 입력하세요");

    try {
      const response = await fetch("http://localhost:5000/user/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_id: id, user_pw: password }),
      });
      const data = await response.json();

      if (response.ok) {
        onLoginSuccess();
        navigate("/");
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Login error:", error);
      alert("로그인 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="login">
      <h2>로그인</h2>
      <form onSubmit={handleLogin}>
        <label>
          아이디:{" "}
          <input
            type="text"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </label>
        <label>
          비밀번호:{" "}
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <button type="submit">로그인</button>
        <a href="/signup">
          <button type="button">회원가입</button>
        </a>
      </form>
    </div>
  );
}

function SignUp() {
  const [name, setName] = useState("");
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");

  const handleSignUp = async (e) => {
    e.preventDefault();
    if (!name || !id || !password) return alert("모든 필드를 입력해주세요");

    try {
      const response = await fetch("http://localhost:5000/user/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_nm: name, user_id: id, user_pw: password }),
      });

      if (response.ok) {
        alert("회원가입 완료");
        setName("");
        setId("");
        setPassword("");
      } else {
        const errorData = await response.json();
        alert(`회원가입 실패: ${errorData.message}`);
      }
    } catch (error) {
      console.error("Signup error:", error);
      alert("회원가입 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="signup">
      <h2>회원가입</h2>
      <form onSubmit={handleSignUp}>
        <label>
          이름:{" "}
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <label>
          아이디:{" "}
          <input
            type="text"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
        </label>
        <label>
          비밀번호:{" "}
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <button type="submit">회원가입</button>
      </form>
    </div>
  );
}

export default App;
